import { useState, useEffect } from "react";
import axios from 'axios';
import { useAuth } from '../path_to_AuthContext/AuthContext'; // Adjust the path based on the actual file location

function Auth() {
  const { setAuthData } = useAuth(); // Accessing setAuthData method from the context
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();
  const [data, setData] = useState(null);

  useEffect(() => {
    axios.defaults.withCredentials = true;

    return () => {
      axios.defaults.withCredentials = false;
    };
  }, []);

  const submit = () => {
    axios
      .post("http://localhost:8080/api/auth/auth", {
        headers: {
          "Content-type": "application/json",
        },
        email: email,
        password: password, // Corrected variable name
      })
      .then((response) => {
        setAuthData(response.data); // Setting authData in the context
        setData(response.data);
      })
      .catch((error) => {
        console.log('Error: ', error);
      });
  };

  return (
    <div className="">
      <input type="email" placeholder="email" onChange={e => setEmail(e.target.value)} /> <br />
      <input type="password" placeholder="password" onChange={e => setPassword(e.target.value)} /> <br /> {/* Adjusted state updater function */}
      <div className="">
        {data}
      </div>

      <input type="button" value={"Submit"} onClick={submit}/> {/* Updated button label */}
    </div>
  );
}

export default Auth;
