import { createSlice } from '@reduxjs/toolkit'

const initialState = {
  value: false,
}

export const isrevSlice = createSlice({
  name: 'isrevSlice',
  initialState,
  reducers: {
    send: (state) => {
      state.value = true
    },

    del: (state) => {
      state.value = false
    }
  },
})

export const { send, del } = isrevSlice.actions

export default isrevSlice.reducer
