import { useState } from "react";
import axios from 'axios';
import { useAuth } from './AuthContext'; // Importing the useAuth hook
import { useEffect } from "react";

function Auth() {
  const { setAuthData } = useAuth(); // Accessing setAuthData method from the context
  const [email, setEmail] = useState();
  const [passwords, setPasswords] = useState();
  const [data, setData] = useState(null);

  useEffect(() => {
    axios.defaults.withCredentials = true;

    return () => {
      axios.defaults.withCredentials = false;
    };
  }, []);

  const submit = () => {
    axios
      .post("http://localhost:8080/api/auth/auth", {
        headers: {
          "Content-type": "application/json",
        },
        email: email,
        passwords: passwords,
      })
      .then((response) => {
        setAuthData(response.data); // Setting authData in the context
        setData(response.data);
      })
      .catch((error) => {
        console.log('Ошибка : ', error);
      });
  };

  return (
    <div className="">
      <input type="email" placeholder="email" onChange={e => setEmail(e.target.value)} /> <br />
      <input type="password" placeholder="password" onChange={e => setPasswords(e.target.value)} /> <br />
      <div className="">
        {data}
      </div>

      <input type="button" value={"отправить"} onClick={submit}/>
    </div>
  );
}

export default Auth;
