import axios from "axios";
import { useEffect, useState } from "react";


function Loadtag() {

    const [data, setData] = useState([])
    // const [tags, setTags] = useState([])
    let tags = []


    useEffect(() => {
        axios.get('http://localhost:8080/api/tag/tags')
            .then((response) => {
                setData(response.data)
            })
            .catch((err) => {
                console.log(err)
            })
    }, [])


    let tagarr = [] 

    const addtag = (e) => {
        const tagname = e.target.name
        
        for(let i; i < tagarr.length; i++) {
            if(tagname == tagarr[i]){
                console.log('было!')

            }
            else{
                tagarr.push(tagname)
                console.log('tagarr в функции = ',tagarr)
            }
        }
    }


    const submit = () => {
        tags = [...tagarr]
        console.log('tags = ',tags)
    }
    
    return (  
        <>
            {data.map((tag) => 
                <ul key={tag.id}>
                    <input type="checkbox" id={tag.id} name={tag.name} onChange={e => addtag(e)}/>
                    <label htmlFor={tag.id}>{tag.name}</label>
                </ul>
            )}
            <input type="submit" value={"вставить тэги"} onClick={submit}/> <br />
        </>
    );
}

export default Loadtag;
