import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getOneAnime } from "../../api";

function OneAnime() {
    const { id } = useParams();
    const [data, setData] = useState({});

    useEffect(() => {
        getOneAnime(id)
            .then(response => {
                setData(response);
                console.log('data = ', data);
            })
            .catch(err => {
                console.log(err);
            });
    }, [id]);

    if (data) {
        const {
            name,
            animeurl,
            dates,
            numep,
            rating,
            altername,
            studiourl,
            description
        } = data;

        return (
            <main className="oneanime">
                <div className="aniname">{name}</div>

                <img src={animeurl} alt="" className="animg" />
                <button className="bookmarks">Добавить в избранное</button>

                <div className="info">
                    <p>Информация</p>
                    <div className="genres">Жанр: драма, ужасы</div>
                    <div className="year">{dates}</div>
                    <div className="col-anime">{numep}</div>
                    <div className="rating">{rating}</div>
                    <div className="alt-name">
                        Альтернативные названия: {altername}
                    </div>
                </div>

                <div className="studio">
                    <p>Студия</p>
                    <img src={studiourl} alt="" className="studio-img" />
                </div>

                <div className="desc">
                    <h1>Описание</h1>
                    <p>{description}</p>
                </div>

                <div className="rew-h">Отзывы</div>
            </main>
        );
    } else {
        return null;
    }
}

export default OneAnime;
