import { useState, useEffect } from "react"
import { Link, Outlet } from "react-router-dom"
import axios from "axios"
import { createContext } from "react"

function Header() {
    const [data, setData] = useState()
    const isauth = createContext(false)
    
    console.log('data is not = ')

    useEffect(() => {
        axios
            .get("http://localhost:8080/api/auth/cook", {
                withCredentials: true
            })
            
            .then((response) => {
                console.log('data yes = ',data)
                setData(response.data)
            })
            
            .catch((error) => {
                console.log('Ошибка : ',error)
            })
    })



    return (  
        <>
            <header className="header">
                <div class="logo">
                    <Link to="/main">アニ AniWeb</Link>
                </div>
                <div className="search">
                    <input type="text" placeholder="Поиск" />
                    <img src="" alt="" />
                </div>
                
                <isauth.Provider value={data}>
                    <div className="profile">
                            {data 
                                ?<Link to="/profile" >{data}</Link>
                                :<><Link to={"/reg"}>Регистрация</Link><div> - </div><Link to={"/auth"}>Авторизация</Link></>
                            }
                    </div>
                </isauth.Provider>

                <div className="burger">
                    <Link to="/Catalog">Catalog</Link>
                </div>
            </header>
            <Outlet />
        </>
    );
}

export default Header;
