import { createSlice } from '@reduxjs/toolkit'

const initialState = {
  value: false,
}

export const isrevSlice = createSlice({
  name: 'isrevSlice',
  initialState,
  reducers: {
    sendrev: (text) => {
      state.value = text
    },

    delrev: (state) => {
      state.value = state
    }
  },
})

export const { sendrev, delrev } = isrevSlice.actions

export default isrevSlice.reducer
