import axios from "axios";
import { useEffect, useState } from "react";
import { getAllAnime } from "../../../api";

function Loadtag() {

    const [data, setData] = useState([])
    const [tags, setTags] = useState([])
    const [id, setid] = useState()


    useEffect(() => {
        axios.get('http://localhost:8080/api/tag/tags')
            .then((response) => {
                setData(response.data)
                console.log(data.length)
            })
            .catch((err) => {
                console.log(err)
            })
    }, [])

    useEffect(() => {
        getAllAnime()
            .then((response) => {
                setid(response.data)
            })
            .catch((err) => {
                console.log(err)
            })
    }, [])

    console.log('id = ', id.length)

    const handleCheckboxChange = (e) => {
        const tagname = e.target.id;
        if (e.target.checked) {
            setTags([...tags, tagname]);
        } else {
            setTags(tags.filter((id) => id !== tagname));
        }

    };

    const submit = () => {
        console.log('tags = ',tags)
        const id = id.length + 1

        axios.post(`http://localhost:8080/api/tag/anitags/create/${id}`, {
            tags: tags
        })
    }
    
    return (  
        <>
            {data.map((tag) => 
                <ul key={tag.id}>
                    <input type="checkbox" id={tag.id} name={tag.name} onChange={handleCheckboxChange}/>
                    <label htmlFor={tag.id}>{tag.name}</label>
                </ul>
            )}
            <input type="submit" value={"вставить тэги"} onClick={submit}/> <br />
        </>
    );
}

export default Loadtag;
