import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getOneAnime } from "../../api";

function OneAnime() {
    const { id } = useParams();
    const [data, setData] = useState({});

    useEffect(() => {
        getOneAnime(id)
            .then(response => {
                setData(response);
                console.log('data = ', data);
            })
            .catch(err => {
                console.log(err);
            });
    }, [id]);

    if (Object.keys(data).length !== 0) {
        return (
            <main className="oneanime">
                {Object.keys(data).map(key => (
                    <div key={key} className="aniname">
                        {data[key]}
                    </div>
                ))}

                <button className="bookmarks">Добавить в избранное</button>

                <div className="info">
                    <p>Информация</p>
                    {Object.keys(data).map(key => (
                        <div key={key} className="genres">
                            Жанр: драма, ужасы
                        </div>
                    ))}
                    {/* Остальные поля информации */}
                </div>

                <div className="studio">
                    <p>Студия</p>
                    {Object.keys(data).map(key => (
                        <img
                            key={key}
                            src={data[key]}
                            alt=""
                            className="studio-img"
                        />
                    ))}
                </div>

                <div className="desc">
                    <h1>Описание</h1>
                    <p>{data.description}</p>
                </div>

                <div className="rew-h">Отзывы</div>
            </main>
        );
    } else {
        return null;
    }
}

export default OneAnime;
