import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getOneAnime } from "../../api";

function OneAnime() {

    const {id} = useParams()
    const {data, setData} = useState([])



    useEffect(() => {
        getOneAnime(id)
            .then(response => {
                setData(response.data)
            })

            .catch(err => {
                console.log(err)
            })
    }, [id, data])

    console.log(data)

    return (  
        <>       
            {
                data.map((anime) =>
                    <main classNameName="oneanime">

                        <div className="aniname">{anime.name}</div>
                            
                        <img src={anime.animeurl} alt="" className="animg" />
                        <button className="bookmarks">Добавить в избранное</button>

                        <div className="info">
                            
                            <p>Информация</p>

                            <div className="genres">Жанр: драма, ужасы</div>
                            <div className="year">{anime.dates}</div>
                            <div className="col-anime">{anime.numep}</div>
                            <div className="rating">{anime.rating}</div>
                            <div className="alt-name">Альтернативные названия: {anime.altername}</div>
                        </div>

                        <div className="studio">
                            <p>Студия</p>
                            <img src={anime.studiourl} alt="" className="studio-img" />
                        </div>


                        <div className="desc">
                            <h1>Описание</h1>
                            <p>{anime.description}</p>
                        </div>

                        <div className="rew-h">Отзывы</div>
                    </main>

                )
            }
        </>
    );
}

export default OneAnime;
