import axios from "axios";
import { useParams } from "react-router-dom";

function Addbook() {
    
    const {id} = useParams()

    function click() {
        alert('вы добавили закладку!')
    }

    const add = () => {
        axios 
            .post(`http://localhost:8080/api/book/${id}`)
    }
    
    
    return (  
        <>
            <button className="bookmarks" onClick={click}>Добавить в избранное</button>
        </>
    );
}

export default Addbook;
