import { useState, useEffect } from "react";
import { Link, Outlet } from "react-router-dom";
import axios from "axios";
import { useAuth } from './profile/auth-Reg/AuthProvider'; // Importing the useAuth hook

function Header() {
  const { authData } = useAuth(); // Accessing authData from the context

  useEffect(() => {
    axios
      .get("http://localhost:8080/api/auth/cook", {
        withCredentials: true
      })
      .then((response) => {
        setAuthData(response.data); // Setting authData in the context
      })
      .catch((error) => {
        console.log('Ошибка : ', error);
      });
  }, []); // Add an empty array as the second argument to useEffect to run only once

  return (
    <>
      <header className="header">
        <div class="logo">
          <Link to="/main">アニ AniWeb</Link>
        </div>
        <div className="search">
          <input type="text" placeholder="Поиск" />
          <img src="" alt="" />
        </div>

        <div className="profile">
          {authData ? (
            <Link to="/profile">{authData}</Link>
          ) : (
            <>
              <Link to={"/reg"}>Регистрация</Link>
              <div> - </div>
              <Link to={"/auth"}>Авторизация</Link>
            </>
          )}
        </div>

        <div className="burger">
          <Link to="/Catalog">Catalog</Link>
        </div>
      </header>
      <Outlet />
    </>
  );
}

export default Header;
